import React, {Component} from 'react';
import puppy1 from "./puppies/puppy1.jpeg";

class Display extends Component {
    constructor(){
        super();
        const img1 = require('./puppies/puppy1.jpeg');
        const img2 = require('./puppies/puppy2.jpeg');
        const img3 = require('./puppies/puppy3.jpeg');
        const img4 = require('./puppies/puppy4.jpeg');
        const img5 = require('./puppies/puppy5.jpeg');
        const images = [
          img1,
          img2,
          img3,
          img4,
          img5
        ];
        this.state = {
            image: {puppy1},
            details: "I'm Puppy 1"
        };
    }

    componentDidMount(){
        this.displayImage();
    }

    displayImage() {
      var randomNum = Math.floor(Math.random() * 5) + 1;
      var newImage = "./puppies/puppy1.jpeg"
      this.setState({
            image : newImage,
            details: "I'm Puppy " + randomNum
        });
    }

    render() {
        return (
            <div>
              <button type="button" className="btn" onClick={this.displayImage.bind(this)}>Next</button>
              <br/>
              <h2>Image:</h2>
              <img src={this.state.image}/>
              <br/>
              <h2>Details: {this.state.details}</h2>
            </div>
        );
    }
}

export default Display;
