import React, {Component} from 'react';
import puppy0 from "./puppies/puppy0.jpeg";

class Display extends Component {
    constructor(){
        super();
        this.state = {
            image: {puppy0},
            details: "puppy 0"
        };
    }

    componentDidMount(){
        this.displayImage();
    }

    getRandomInt(min,max)
    {
      randomNum = Math.floor(Math.random() * (max - min + 1)) + min;
      return randomNum;
    }

    displayImage() {
      var randomNum = getRandomInt(1, 4);
      var newImage = "./puppies/puppy" + randomNum + ".jpeg"
      this.setState({
            image : newImage,
            details: "puppy " + randomNum
        });
    }

    render() {
        return (
            <div>
              <button type="button" className="btn" onClick={this.displayImage.bind(this)}>Next</button>
              <br/>
              <h2>Image:</h2>
              <img src={this.state.image}/>
              <br/>
              <h2>Details: {this.state.details}</h2>
            </div>
        );
    }
}

export default Display;
