import React, {Component} from 'react';

class Display extends Component {
    constructor(){
        super();
        const img1 = require('./puppies/puppy1.jpeg');
        const img2 = require('./puppies/puppy2.jpeg');
        const img3 = require('./puppies/puppy3.jpeg');
        const img4 = require('./puppies/puppy4.jpeg');
        const img5 = require('./puppies/puppy5.jpeg');
        this.state = {
            image: [img1, img2, img3, img4, img5],
            index: 1
        };
    }

    componentDidMount(){
        this.displayImage();
    }

    displayImage() {
      var randomNum = Math.floor(Math.random() * 5) + 1;
      this.setState({
            index: randomNum
        });
    }

    render() {
        return (
            <div>
              <button type="button" className="btn" onClick={this.displayImage.bind(this)}>Next</button>
              <br/>
              <h2>Image:</h2>
              <img src={this.state.images[this.state.index]}/>
              <br/>
              <h2>Details: {this.state.index}</h2>
            </div>
        );
    }
}

export default Display;
