import React, {Component} from 'react';
import { Button } from 'react-bootstrap';

class Display extends Component {
    constructor(){
        super();
        const img0 = require('./puppies/puppy0.jpeg');
        const img1 = require('./puppies/puppy1.jpeg');
        const img2 = require('./puppies/puppy2.jpeg');
        const img3 = require('./puppies/puppy3.jpeg');
        const img4 = require('./puppies/puppy4.jpeg');
        this.state = {
            images: [img0, img1, img2, img3, img4],
            details: ["white", "yellow", "black", "colorful", "brown"],
            index: 1
        };
    }

    componentDidMount(){
        this.displayImage();
    }

    displayImage() {
      var randomNum = Math.floor(Math.random() * 5);
      this.setState({
            index: randomNum
        });
    }

    render() {
        return (
            <div class="panel panel-default">
              <div class="panel-body">
                <h1>Welcome to the Random Puppies App</h1>
                <Button type="button" className="btn float-right" onClick={this.displayImage.bind(this)}>Next</Button>
              </div>
              <div class="panel-body">
                <h2>Image:</h2>
                <img src={this.state.images[this.state.index]} alt="puppy"/>
                <br/>
                <h2>Details: We are {this.state.details[this.state.index]} puppies!</h2>
              </div>
            </div>
        );
    }
}

export default Display;
